import { GlobalContext } from './context/GlobalContext';


const Transaction = (props) => {

    return (
        <div className='TransactionItem' style={ props.amount > 0 ? { borderRight: "5px solid red" } : {borderRight: "5px solid green"}}>

            <div className='info'>
                <h3>{props.name}</h3>
                <h2 >$ {props.amount} </h2>
            </div>

            <div className='btnClass'>
                <button className='delete-btn'>X</button>
            </div>

        </div>

    )
}

export default Transaction;
